mapper.select:
SELECT nnum, ntitle, ncontent, cnt, word, rdate, nimage, nimagesaved, nsize
FROM notice
WHERE nnum=#{nnum}

mapper.update:
UPDATE notice
SET ntitle=#{ntitle}, ncontent=#{ncontent}, word=#{word}, rdate=sysdate, 
      nimage=#{nimage}, nimagesaved=#{nimagesaved}, nsize=#{nsize}
WHERE nnum=#{nnum}
